mapper.update:
update share_board
   set remove_date = now()
 where board_no = #{boardNo}

update share_board
   set update_date = now(),
   board_title = #{boardTitle},
   board_content = #{boardContent}
 where board_no = #{boardNo}

mapper.select:
	SELECT *
	  FROM share_attraction s
	  LEFT JOIN (
                 SELECT *
  				   FROM share_board
  				  WHERE user_no = #{userNo}
				) b 
	    ON s.share_no = b.user_no
	 WHERE b.user_no IS NOT NULL;

mapper.update:
update share_board
   set board_hit = board_hit + 1
 where board_no = #{boardNo}
