mapper.update:
UPDATE story_board
   SET remove_date = now()
 WHERE story_board_no = #{storyBoardNo}

UPDATE story_board
   SET update_date = now(),
   	   story_board_title = #{storyBoardTitle},
       story_board_content = #{storyBoardContent}
 WHERE story_board_no = #{storyBoardNo}

UPDATE story_board
   SET story_board_hit = story_board_hit + 1
 WHERE story_board_no = #{storyBoardNo}
